import React, {useEffect, useReducer, useState} from 'react';
import {Route, BrowserRouter as Router, Routes} from 'react-router-dom'
import 'bootstrap/dist/css/bootstrap.min.css';
import NaviBar from "./Components/NaviBar";
import About from "./Pages/About";
import Rent from "./Pages/Rent";
import DetailedTruck from "./Pages/DetailedTruck";
import {Context, defaultState} from './Supporting Files/context'
import {LOAD_TRUCKS, reducer} from "./Supporting Files/reducer";

function App() {
    const [state, dispatch] = useReducer(reducer, defaultState)

    useEffect(() => {
        dispatch({
            type: LOAD_TRUCKS,
            payload: {}
        })
    }, [])

    useEffect(() => {
        localStorage.setItem('selectedCards', JSON.stringify(selectedCards))
    }, [selectedCards])

    const onClickSelect = (id) => {
        if (selectedCards.includes(id)) {
            setSelectedCards(prev => prev.filter(_id => _id != id));
        } else {
            setSelectedCards((prev) => [...prev, id])
        }
    }

   /* const fetchTrucks = async () => {
        const res = await fetch(`/api/cars/`)
            .then((response) => {
                return response.json();
            }).catch(() => {
                return {resultCount: 0, results: []}
            })
        console.log(res)
        setTrucks(res)
        fetchBrands(res)
    }

    const fetchBrands = async (tempTrucks) => {
        const res = []
        console.log(tempTrucks)
        for (const element of tempTrucks) {
            const tempRes = await fetch(`/api/brands/${element.brand}`)
                .then((response) => {
                    return response.json();
                }).catch(() => {
                    return {resultCount: 0, results: []}
                })
            res.push(tempRes.title)
        }
        console.log(res)
        setBrands(res)
    }*/

    const fetchTruck = async (truckID) => {
        const res = await fetch(`/api/cars/${truckID}`)
            .then((response) => {
                return response.json();
            }).catch(() => {
                return {resultCount: 0, results: []}
            })
        console.log(res)
        setTruck(res)
        fetchBrand(res.brand)
    }

    const fetchBrand = async (brandID) => {
        const res = await fetch(`/api/brands/${brandID}`)
            .then((response) => {
                return response.json();
            }).catch(()=>{
                return {resultCount:0, results:[]}
            })
        console.log(res)
        setBrand(res)
    }

    return (
        <Context.Provider value={{
            onClickSelect,
            fetchTrucks,
            fetchTruck,
            fetchBrand,
            fetchBrands
        }}>
            <Router>
                <React.StrictMode>
                    <NaviBar/>
                    <Routes>
                        <Route exact path="/about" element={<About/>}/>
                        <Route  path="/" element={<Rent trucks={trucks} brands={brands} selectedCards={selectedCards}/>}/>
                        <Route exact path="rent/:id" element={<DetailedTruck/>}/>
                    </Routes>
                </React.StrictMode>
            </Router>
        </Context.Provider>
);
}

export default App;
