import Table from 'react-bootstrap/Table';
import {Button} from "react-bootstrap";
import TruckCard from "../Components/TruckCard";
import React, {useContext, useEffect} from "react";
import {Context} from "../Supporting Files/context";
import {useParams} from "react-router-dom";
import {LOAD_ORDERS} from "../Supporting Files/reducer";

export const StatusEnum = {
    Pending: "ожидает подтверждения",
    Confirmed: "подтвержден",
    Denied: "отклонен",
    InProgress: "Выполняется",
    Done: "Выполнен"
}

function History() {
    const {id} = useParams()

    const {
        fetchOrders,
        dispatch,
        state
    } = useContext(Context)

    useEffect(()=>{
        fetchOrders(id).then(orders => {
            console.log("fetchOrders")
            console.log(orders)
            dispatch({
                type: LOAD_ORDERS,
                payload: { orders: orders }
            })
        })
    }, [])


    if (state.isAuthenticated) {
        if (!state.isManager) {
            return (
                <Table striped bordered hover style={{
                    width: "60%",
                    textAlign: "center",
                    margin: "24px auto"
                }}>
                    <thead>
                    <tr>
                        <th>Номер заказа</th>
                        <th>Стоимость р.</th>
                        <th>Дата заказа</th>
                        <th>Статус</th>
                    </tr>
                    </thead>
                    <tbody>
                    {state.orders.map((item) => {
                        return <tr>
                            <th>{item.pk}</th>
                            <th>{item.price}</th>
                            <th>{(new Date(item.time)).toLocaleDateString()}</th>
                            <th>{item.status}</th>
                        </tr>
                    })}
                    </tbody>
                </Table>
            );
        } else {
            return (
                <Table striped bordered hover style={{
                    width: "60%",
                    textAlign: "center",
                    margin: "24px auto"
                }}>
                    <thead>
                    <tr>
                        <th>Номер заказа</th>
                        <th>Стоимость р.</th>
                        <th>Дата заказа</th>
                        <th>Статус</th>
                        <th>Решить</th>
                    </tr>
                    </thead>
                    <tbody>
                    {state.orders.map((item, index) => {
                        return <tr>
                            <th>{item.pk}</th>
                            <th>{item.price}</th>
                            <th>{(new Date(item.time)).toLocaleDateString()}</th>
                            <th>{item.status}</th>
                            <th>
                            { item.status == StatusEnum.Pending &&
                                <>
                                    <Button variant="success" className="col-6">Подтвердить</Button>
                                    <Button variant="danger" className="col-6">Отклонить</Button>
                                </>
                            }
                            </th>
                        </tr>
                    })}
                    </tbody>
                </Table>
            );
        }
    } else {
        return (<h2 style={{
            textAlign: "center",
            marginTop: "16px"
        }}>Авторизируйтесь, чтобы посмотреть список покупок</h2>)
    }
}

export default History;
