import React, {useContext, useEffect, useState} from 'react';
import {Card, Col, Row, Button, Spinner} from "react-bootstrap";
import getData from "../Components/data";
import ListGroup from "react-bootstrap/ListGroup";
import {Link, useParams} from "react-router-dom";
import {Breadcrumbs} from "@mui/material";
import {Context} from "../Supporting Files/context";
import {LOAD_TRUCK, LOAD_TRUCKS} from "../Supporting Files/reducer";


const cardStyle = {
    margin: "8px 30%",
    textColor: "black",
    textAlign: "center"
};

const cardImage = {
    width: "100%",
    height: "500px",
};

function DetailedTruck() {
    const {id} = useParams();

    const {
        fetchTruck,
        fetchBrand,
        dispatch,
        state
    } = useContext(Context)

    const [data, setData] = useState({})
    const [brand, setBrand] = useState({})

    useEffect(()=>{
        fetchTruck(id).then(truck => {
            fetchBrand(truck.pk).then(brand => {
                dispatch({
                    type: LOAD_TRUCK,
                    payload: {
                        truck: truck,
                        brand: brand
                    }
                })
            })
        })
    },[])

   /* useEffect(()=>{
        const  getTruck = async () => {
            const res = await fetch(`/api/cars/${id}`)
                .then((response) => {
                    return response.json();
                }).catch(()=>{
                    return {resultCount:0, results:[]}
                })
            console.log(res)
            setData(res)

            const resBrand = await fetch(`/api/brands/${res.brand}`)
                .then((response) => {
                    return response.json();
                }).catch(()=>{
                    return {resultCount:0, results:[]}
                })
            console.log(resBrand)
            setBrand(resBrand)
        }

        getTruck();
    },[])*/

    return (
        <>
            <Breadcrumbs style={cardStyle} aria-label='breadcrumb'>
                <Link style={{textDecoration: 'none'}} to='/'>Rent</Link>
                <Link style={{textDecoration: 'none'}} to={`/rent/${id}`}>{state.brand.title + " " + state.truck.title}</Link>
            </Breadcrumbs>
            <Card style={cardStyle}>
                <Card.Img style={cardImage} className="img-fluid" variant="top" src={state.truck.photo} height={100} width={100}/>
                <Card.Body>
                    <Card.Title>{state.brand.title + " " + state.truck.title}</Card.Title>
                </Card.Body>
                <ListGroup className="list-group-flush">
                    <ListGroup.Item>Объём: {state.truck.capacity}м<sup>3</sup></ListGroup.Item>
                    <ListGroup.Item>Стоимость: {state.truck.price}р.</ListGroup.Item>
                    <ListGroup.Item>Страна: {state.brand}</ListGroup.Item>
                    <ListGroup.Item>Описание: {state.truck.description}</ListGroup.Item>
                    <ListGroup.Item>Грузоподъемность: {state.truck.payload} т.</ListGroup.Item>
                </ListGroup>
            </Card>
        </>
    );
}

export default DetailedTruck;
