import React, {useEffect, useState} from 'react';
import {Card, Col, Row, Button, Spinner} from "react-bootstrap";
import TruckCard from "../Components/TruckCard";
import IntervalSelector from "../Components/IntervalSelector";
import getData from "../Components/data";

const TableBlockStyle = {
    margin: "20px 15%",
    textAlign: 'center',
}

const TableCardStyle = {
    display: "inline-block",
    margin: "8px 8px 8px 0px",
}

function Rent() {
    const [selectedCards, setSelectedCards] = useState([])
    const [trucks, setTrucks] = useState([])
    const [brand, setBrand] = useState([])

    useEffect(()=>{
        const  getTrucks = async () => {
            const res = await fetch(`/api/cars/`)
                .then((response) => {
                     return response.json();
                }).catch(()=>{
                    return {resultCount:0, results:[]}
                })
            console.log(res)
            setTrucks(res)

            for (const element of res) {
                const brand = await fetch(`/api/brands/${res.brand}`)
                    .then((response) => {
                        return response.json();
                    }).catch(()=>{
                        return {resultCount:0, results:[]}
                    })
                console.log(brand)
                setBrand((prev) => [...prev, id])
            }
        }


        getTrucks();
    },[])

    const onClickSelect = (id) => {
        if (selectedCards.includes(id)) {
            setSelectedCards(prev => prev.filter(_id => _id != id));
        } else {
            setSelectedCards((prev) => [...prev, id])
        }
    }

    return (
        <>
            <IntervalSelector/>
            {!trucks.length && <div>
                <h1>К сожалению, пока нет свободного транспорта :(</h1>
            </div>}

            <Row className="g-4" style={TableBlockStyle}>
                {trucks.map((item)=>{
                    return<Col>
                        <TruckCard id={item.pk}
                                   name={item.title}
                                   brand={brand.find(item.brand)}
                                   price={item.price}
                                   capacity={item.capacity}
                                   image={item.photo}
                                   isSelected={selectedCards.includes(item.pk)}
                                   onClickSelect={onClickSelect}/>
                    </Col>
                })}
            </Row>
        </>
    );
}

export default Rent;
