import React, {useEffect, useReducer} from 'react';
import {Route, BrowserRouter as Router, Routes} from 'react-router-dom'
import 'bootstrap/dist/css/bootstrap.min.css';
import NaviBar from "./Components/NaviBar";
import About from "./Pages/About";
import Rent from "./Pages/Rent";
import DetailedTruck from "./Pages/DetailedTruck";
import {
    reducer,
    GET_SELECTED_TRUCKS_FROM_LOCAL,
    SET_SELECTED_TRUCKS_FOR_LOCAL
} from "./Supporting Files/reducer";
import {defaultState, Context} from "./Supporting Files/context";
import History from "./Pages/History";

function App() {
    const [state, dispatch] = useReducer(reducer, defaultState);

    useEffect(() => {
        dispatch({
            type: GET_SELECTED_TRUCKS_FROM_LOCAL,
            payload: {}
        })
    }, [])

    useEffect(()=>{
        console.log("STATE RELOAD")
        console.log(state)
    },[state])

    useEffect(() => {
        console.log("APP")
        console.log(state)
        dispatch({
            type: SET_SELECTED_TRUCKS_FOR_LOCAL,
            payload: {}
        })
    }, [state.selectedTrucks])

    /*const onClickSelect = (id) => {
        if (selectedCards.includes(id)) {
            setSelectedCards(prev => prev.filter(_id => _id != id));
        } else {
            setSelectedCards((prev) => [...prev, id])
        }
    }*/

    const fetchTrucks = async () => {
        const res = await fetch(`/api/cars/`)
            .then((response) => {
                return response.json();
            }).catch(() => {
                return {resultCount: 0, results: []}
            })
        console.log(res)
        return res
    }

    const fetchBrands = async (tempTrucks) => {
        const res = []
        console.log(tempTrucks)
        for (const element of tempTrucks) {
            const tempRes = await fetch(`/api/brands/${element.brand}`)
                .then((response) => {
                    return response.json();
                }).catch(() => {
                    return {resultCount: 0, results: []}
                })
            res.push(tempRes.title)
        }
        console.log(res)
        return res
    }

    const fetchTruck = async (truckID) => {
        const res = await fetch(`/api/cars/${truckID}`)
            .then((response) => {
                return response.json();
            }).catch(() => {
                return {resultCount: 0, results: []}
            })
        console.log("fetchTruck")
        console.log(res)
        return res
    }

    const fetchBrand = async (brandID) => {
        const res = await fetch(`/api/brands/${brandID}`)
            .then((response) => {
                return response.json();
            }).catch(()=>{
                return {resultCount:0, results:[]}
            })
        console.log("fetchBrand")
        console.log(res)
        return res
    }

    const fetchOrders = async (userID) => {
        var res = await fetch(`/api/orders`)
            .then((response) => {
                return response.json();
            }).catch(()=>{
                return {resultCount:0, results:[]}
            })
        res = res.filter(_id => _id == userID)
        console.log("fetchOrders")
        console.log(res)
        return res
    }

    return (
        <Context.Provider value={{
            fetchTrucks,
            fetchBrands,
            fetchTruck,
            fetchBrand,
            state, dispatch
        }}>
            <Router>
                <React.StrictMode>
                    <NaviBar/>
                    <Routes>
                        <Route exact path="/about" element={<About/>}/>
                        <Route  path="/" element={<Rent/>}/>
                        <Route exact path="rent/:id" element={<DetailedTruck/>}/>
                        <Route exact path="history" element={<History/>}/>
                    </Routes>
                </React.StrictMode>
            </Router>
        </Context.Provider>
);
}

export default App;
