import React, {useContext, useEffect} from 'react';
import {Col, Row} from "react-bootstrap";
import TruckCard from "../Components/TruckCard";
import IntervalSelector from "../Components/IntervalSelector";
import {LOAD_TRUCKS} from "../Supporting Files/reducer";
import {Context} from "../App";

const TableBlockStyle = {
    margin: "20px 15%",
    textAlign: 'center',
}

const TableCardStyle = {
    display: "inline-block",
    margin: "8px 8px 8px 0px",
}

function Rent() {
    const {
        fetchTrucks,
        fetchBrands,
        state,
        dispatch } = useContext(Context)

    useEffect(()=>{
        fetchTrucks().then(trucks => {
            fetchBrands(trucks)
            dispatch({
                type: LOAD_TRUCKS,
                payload: {}
            })
        })
    },[])

    useEffect(()=>{
        console.log("STATE RELOAD")
        console.log(state)
    },[state])

    return (
        <>
            <IntervalSelector/>
            {
                console.log("STATE RENT")
            }
            {
                console.log(state)
            }
            <Row className="g-4" style={TableBlockStyle}>
                {state.trucks.map((item, index)=>{
                    return<Col>
                        <TruckCard id={item.pk}
                                   name={item.title}
                                   price={item.price}
                                   brand={state.brands[index]}
                                   capacity={item.capacity}
                                   image={item.photo}
                                   isSelected={state.selectedCards.includes(item.pk)}/>
                    </Col>
                })}
            </Row>
        </>
    );
}

export default Rent;
