import {defaultState} from "./context";

export const
    TOGGLE_TRUCK = 'TOGGLE_TRUCK',
    LOAD_TRUCKS = 'LOAD_TRUCKS',
    LOAD_TRUCK = 'LOAD_TRUCK',
    GET_SELECTED_TRUCKS_FROM_LOCAL = 'GET_SELECTED_TRUCKS_FROM_LOCAL',
    SET_SELECTED_TRUCKS_FOR_LOCAL = 'SET_SELECTED_TRUCKS_FROM_LOCAL',
    LOAD_ORDERS = "LOAD_ORDERS",
    SWITCH_ID = "SWITCH_ID"

export const reducer = (state, action) => {
// type, payload
    const { type, payload } = action
    switch(type) {
        case SWITCH_ID:
            return {
                ...state,
                selectedTrucks: [],
                id: state.id == 0 ? 1 : 0
            }
        case LOAD_ORDERS:
            return {
                ...state,
                orders: payload.orders
            }
        case LOAD_TRUCKS:
            return {
                ...state,
                trucks: payload.trucks,
                brands: payload.brands
            }
        case LOAD_TRUCK:
            console.log("LOAD_TRUCK SUCCESS")
            return {
                ...state,
                truck: payload.truck,
                brand: payload.brand
            }
        case TOGGLE_TRUCK:
            if (state.selectedTrucks.includes(payload.id)) {
                return {
                    ...state,
                    selectedTrucks: state.selectedTrucks.filter(id => id != payload.id)
                }
            } else {
                return {
                    ...state,
                    selectedTrucks: [...state.selectedTrucks, payload.id],
                }
            }

        case GET_SELECTED_TRUCKS_FROM_LOCAL:
            const raw = localStorage.getItem(`BMSTU USER: ${state.id}`) || []
            const prepared = JSON.parse(raw)
            if (prepared.id == state.id) {
                return {
                    ...state,
                    selectedTrucks: prepared.selectedTrucks,
                }
            } else {
                return state
            }

        case SET_SELECTED_TRUCKS_FOR_LOCAL:
            console.log(`BMSTU USER: ${state.id}`)
            localStorage.setItem(`BMSTU USER: ${state.id}`, JSON.stringify({
                id: state.id,
                selectedTrucks: state.selectedTrucks
            }))
            return state
        default:
            return state
    }
}
