import React, {useContext, useEffect, useState} from 'react';
import {Card, Col, Row, Button, Spinner} from "react-bootstrap";
import TruckCard from "../Components/TruckCard";
import IntervalSelector from "../Components/IntervalSelector";
import getData from "../Components/data";
import {Context} from '../Supporting Files/context'
import {LOAD_BRANDS, LOAD_TRUCKS} from "../Supporting Files/reducer";

const TableBlockStyle = {
    margin: "20px 15%",
    textAlign: 'center',
}

const TableCardStyle = {
    display: "inline-block",
    margin: "8px 8px 8px 0px",
}

function Rent() {
    const { state, dispatch } = useContext(Context)

    useEffect(()=>{
        dispatch({
            type: LOAD_TRUCKS,
            payload: {}
        })
    },[])

    return (
        <>
            <IntervalSelector/>

            <Row className="g-4" style={TableBlockStyle}>
                {state.trucks.map((item, index)=>{
                    return<Col>
                        <TruckCard id={item.pk}
                                   name={item.title}
                                   price={item.price}
                                   brand={state.brands[index]}
                                   capacity={item.capacity}
                                   image={item.photo}
                                   isSelected={state.selectedCards.includes(item.pk)}/>
                    </Col>
                })}
            </Row>
        </>
    );
}

export default Rent;
