export const
    TOGGLE_TRUCK = 'TOGGLE_TRUCK',
    LOAD_TRUCKS = 'LOAD_TRUCKS',
    LOAD_BRANDS = 'LOAD_BRANDS',
    GET_SELECTED_TRUCKS_FROM_LOCAL = 'GET_SELECTED_TRUCKS_FROM_LOCAL',
    SET_SELECTED_TRUCKS_FOR_LOCAL = 'SET_SELECTED_TRUCKS_FROM_LOCAL'

export const reducer = (state, action) => {
// type, payload
    const { type, payload } = action
    switch(type) {
        case LOAD_TRUCKS:
            const fetchTrucks = async () => {
                const resTrucks = await fetch(`/api/cars/`)
                    .then((response) => {
                        return response.json();
                    }).catch(() => {
                        return {resultCount: 0, results: []}
                    })
                    const resBrands = []
                    console.log(state)
                    for (const element of payload.trucks) {
                        const tempRes = await fetch(`/api/brands/${element.brand}`)
                            .then((response) => {
                                return response.json();
                            }).catch(() => {
                                return {resultCount: 0, results: []}
                            })
                        resBrands.push(tempRes.title)
                    }
                    return { brands: resBrands, trucks: resTrucks, ...state }
                console.log(resBrands)
            }

            fetchTrucks()
        case LOAD_BRANDS:
            console.log(state)


        case TOGGLE_TRUCK:
            if (state.selectedTrucks.includes(payload.id)) {
                state.selectedTrucks = state.selectedTrucks.filter(id => id != payload.id)
            } else {
                state.selectedTrucks = [ ...state.selectedTrucks, payload.id]
            }

        case GET_SELECTED_TRUCKS_FROM_LOCAL:
            const raw = localStorage.getItem('selectedCards') || []
            return { selectedTrucks: JSON.parse(raw) }

        case SET_SELECTED_TRUCKS_FOR_LOCAL:
            localStorage.setItem('selectedCards', JSON.stringify(state.selectedTrucks))

        default:
            return state
    }
}
