    axios.put(`http://localhost:8000/api/cars/${truck.pk}/`, truck, config)
        .then(function (response) {
            console.log(response);
        })
        .catch(function (error) {
            console.log(error);
            alert("Произошла ошибка при отправке.\nВозможно, вы не выбрали одно из полей.")
