import React, {useContext, useEffect} from 'react';
import {Card, Form} from "react-bootstrap";
import ListGroup from "react-bootstrap/ListGroup";
import {Link, useParams} from "react-router-dom";
import {Breadcrumbs} from "@mui/material";
import {Context} from "../Supporting Files/context";
import {CHANGE_TRUCK, LOAD_TRUCK, TOGGLE_TRUCK} from "../Supporting Files/reducer";
import {createRoot} from "react-dom/client";
import Button from "react-bootstrap/Button";
import {changeTruck} from "../Supporting Files/NetworkRequests";


const cardStyle = {
    margin: "8px 30%",
    textColor: "black",
    textAlign: "center"
};

const cardImage = {
    width: "100%",
    height: "500px",
};

const confirmButtonStyle = {
    width: "100%",
    backgroundColor: "green",
    borderColor: "green"
};

const removeButtonStyle = {
    width: "100%",
    backgroundColor: "red",
    borderColor: "red"
};

function DetailedTruck() {
    const {id} = useParams();

    const {
        fetchTruck,
        fetchBrand,
        dispatch,
        state
    } = useContext(Context)

    useEffect(() => {
        fetchTruck(id).then(truck => {
                dispatch({
                    type: LOAD_TRUCK,
                    payload: {
                        truck: truck
                    }
                })
        })
        return () => {

        }
    },[])

   /* useEffect(()=>{
        const  getTruck = async () => {
            const res = await fetch(`/api/cars/${id}`)
                .then((response) => {
                    return response.json();
                }).catch(()=>{
                    return {resultCount:0, results:[]}
                })
            console.log(res)
            setData(res)

            const resBrand = await fetch(`/api/brands/${res.brand}`)
                .then((response) => {
                    return response.json();
                }).catch(()=>{
                    return {resultCount:0, results:[]}
                })
            console.log(resBrand)
            setBrand(resBrand)
        }

        getTruck();
    },[])*/

    if (!state.truck) {
        return <></>
    } else {
        if (!state.isManager) {
            return (
                <>
                    <Breadcrumbs style={cardStyle} aria-label='breadcrumb'>
                        <Link style={{textDecoration: 'none'}} to='/'>Rent</Link>
                        <Link style={{textDecoration: 'none'}}
                              to={`/rent/${id}`}>{state.truck.brand + " " + state.truck.title}</Link>
                    </Breadcrumbs>
                    <Card style={cardStyle}>
                        <Card.Img style={cardImage} className="img-fluid" variant="top" src={state.truck.photo}
                                  height={100}
                                  width={100}/>
                        <Card.Body>
                            <Card.Title>{state.truck.brand + " " + state.truck.title}</Card.Title>
                        </Card.Body>
                        <ListGroup className="list-group-flush">
                            <ListGroup.Item>Вместимость: {state.truck.capacity}м<sup>3</sup></ListGroup.Item>
                            <ListGroup.Item>Стоимость: {state.truck.price}р.</ListGroup.Item>
                            <ListGroup.Item>Описание: {state.truck.description}</ListGroup.Item>
                            <ListGroup.Item>Грузоподъемность: {state.truck.payload} т.</ListGroup.Item>
                        </ListGroup>
                    </Card>
                </>
            );
        } else {
            return (
                <>
                    <Breadcrumbs style={cardStyle} aria-label='breadcrumb'>
                        <Link style={{textDecoration: 'none'}} to='/'>Rent</Link>
                        <Link style={{textDecoration: 'none'}}
                              to={`/rent/${id}`}>{state.truck.brand + " " + state.truck.title}</Link>
                    </Breadcrumbs>
                    <Card style={cardStyle}>
                        <Form.Group className="mb-2">
                            <Form.Label >Фото</Form.Label>
                            <Form.Control type="url" value={state.truck.photo} name="url photo" onChange={e => { dispatch({ type: CHANGE_TRUCK, payload: { photo: e.target.value } }) }}/>
                        </Form.Group>
                        <Form.Group className="mb-2">
                            <Form.Label >Брэнд</Form.Label>
                            <Form.Control type="url" placeholder={state.truck.brand} name="brand" onChange={e => { dispatch({ type: CHANGE_TRUCK, payload: { brand: e.target.value } }) }}/>
                        </Form.Group>
                        <Form.Group className="mb-2">
                            <Form.Label >Модель</Form.Label>
                            <Form.Control type="url" placeholder={state.truck.title} name="model" onChange={e => { dispatch({ type: CHANGE_TRUCK, payload: { title: e.target.value } }) }}/>
                        </Form.Group>
                        <Form.Group className="mb-2">
                            <Form.Label >Вместимость, м<sup>3</sup></Form.Label>
                            <Form.Control type="url" placeholder={state.truck.capacity} name="capacity" onChange={e => { dispatch({ type: CHANGE_TRUCK, payload: { capacity: e.target.value } }) }}/>
                        </Form.Group>
                        <Form.Group className="mb-2">
                            <Form.Label >Стоимость, р.</Form.Label>
                            <Form.Control type="url" placeholder={state.truck.price} name="price" onChange={e => { dispatch({ type: CHANGE_TRUCK, payload: { price: e.target.value } }) }}/>
                        </Form.Group>
                        <Form.Group className="mb-2">
                            <Form.Label >Описание</Form.Label>
                            <Form.Control type="url" placeholder={state.truck.description} name="discription" onChange={e => { dispatch({ type: CHANGE_TRUCK, payload: { description: e.target.value } }) }}/>
                        </Form.Group>
                        <Form.Group className="mb-2">
                            <Form.Label >Грузоподъемность, т.</Form.Label>
                            <Form.Control type="url" placeholder={state.truck.payload} name="payload" onChange={e => { dispatch({ type: CHANGE_TRUCK, payload: { payload: e.target.value } }) }}/>
                        </Form.Group>
                        <Button variant="primary"
                                className="mb-2"
                                style={confirmButtonStyle}
                                onClick={
                                    () => changeTruck(state.truck)
                                }>
                            Подтвердить изменения
                        </Button>
                        <Button variant="primary"
                                style={removeButtonStyle}
                                onClick={() => dispatch({
                                })}>
                            Удалить
                        </Button>
                    </Card>

                </>
            );
        }
    }
}

export default DetailedTruck;
