import Table from 'react-bootstrap/Table';
import {Button, Form, Row} from "react-bootstrap";
import TruckCard from "../Components/TruckCard";
import React, {useContext, useEffect} from "react";
import {Context} from "../Supporting Files/context";
import {Link, useParams} from "react-router-dom";
import {CHANGE_STATUS, LOAD_ORDERS, SET_MAX_PRICE, SET_MIN_PRICE} from "../Supporting Files/reducer";
import {changeOrderStatus} from "../Supporting Files/NetworkRequests";

export const StatusEnum = {
    Pending: "ожидает подтверждения",
    Confirmed: "подтвержден",
    Denied: "отклонен",
    InProgress: "Выполняется",
    Done: "Выполнен"
}

function History() {
    const {id} = useParams()

    const {
        fetchOrders,
        dispatch,
        state
    } = useContext(Context)

    useEffect(()=>{
        fetchOrders(id).then(orders => {
            console.log("fetchOrders")
            console.log(orders)
            dispatch({
                type: LOAD_ORDERS,
                payload: { orders: orders }
            })
        })
    }, [])


    if (state.isAuthenticated) {
        if (!state.isManager) {
            return (
                <Table striped bordered hover style={{
                    width: "60%",
                    textAlign: "center",
                    margin: "24px auto"
                }}>
                    <thead>
                    <tr>
                        <th>Номер заказа</th>
                        <th>Стоимость р.</th>
                        <th>Дата заказа</th>
                        <th>Статус</th>
                    </tr>
                    </thead>
                    <tbody>
                    {state.orders.map((item) => {
                        return <tr>
                            <th>{item.pk}</th>
                            <th>{item.price}</th>
                            <th>{(new Date(item.time)).toLocaleDateString()}</th>
                            <th>{item.status}</th>
                        </tr>
                    })}
                    </tbody>
                </Table>
            );
        } else {
            return (
                <>

                <Row>
                    <Form.Select aria-label="Default select example" style={{
                        width: "12%",
                        margin: "auto auto 0 20%"
                    }}>
                        <option>Статус</option>
                        <option>{StatusEnum.Pending}</option>
                        <option>{StatusEnum.Confirmed}</option>
                        <option>{StatusEnum.Denied}</option>
                        <option>{StatusEnum.InProgress}</option>
                        <option>{StatusEnum.Done}</option>
                    </Form.Select>
                    <Form.Group style={{
                        width: "12%",
                        margin: "auto 20% auto auto"
                    }}>
                        <Row>
                            <Form.Group className="col-6">
                                <Form.Label>Min</Form.Label>
                                <Form.Control type="number" placeholder="---" onChange={event => dispatch({ type: SET_MIN_PRICE, payload: { minPrice: event.target.value} })}/>
                            </Form.Group>
                            <Form.Group className="col-6">
                                <Form.Label>Max</Form.Label>
                                <Form.Control type="number" placeholder="---" onChange={event => dispatch({ type: SET_MAX_PRICE, payload: { maxPrice: event.target.value} })}/>
                            </Form.Group>
                        </Row>
                    </Form.Group>
                </Row>
                    <Table striped bordered hover style={{
                        width: "60%",
                        textAlign: "center",
                        margin: "24px auto"
                    }}>
                        <thead>
                        <tr>
                            <th>Номер заказа</th>
                            <th>Стоимость р.</th>
                            <th>Дата заказа</th>
                            <th>Статус</th>
                            <th>Решить</th>
                        </tr>
                        </thead>
                        <tbody>
                        {state.orders.map((item, index) => {
                            return <tr>
                                <th>{item.pk}</th>
                                <th>{item.price}</th>
                                <th>{(new Date(item.time)).toLocaleDateString()}</th>
                                <th>{item.status}</th>
                                <th>
                                { item.status == StatusEnum.Pending &&
                                    <>
                                        <Button variant="success" style={{marginRight: "6px"}}
                                                onClick={() => {changeOrderStatus(item.pk, StatusEnum.Confirmed).then(
                                                    dispatch({type: CHANGE_STATUS, payload: { index: index, status: StatusEnum.Confirmed }})
                                                )}}>Подтвердить</Button>
                                        <Button variant="danger" style={{marginLeft: "6px"}}
                                                onClick={() => {changeOrderStatus(item.pk, StatusEnum.Denied).then(
                                                    dispatch({type: CHANGE_STATUS, payload: { index: index, status: StatusEnum.Denied }})
                                                )}}>Отклонить</Button>
                                    </>
                                }
                                { item.status == StatusEnum.Confirmed &&
                                    <>
                                        <Button variant="success" style={{marginRight: "6px"}}
                                                onClick={() => {changeOrderStatus(item.pk, StatusEnum.InProgress).then(
                                                    dispatch({type: CHANGE_STATUS, payload: { index: index, status: StatusEnum.InProgress }})
                                                )}}>Начать выполнение</Button>
                                        <Button variant="danger" style={{marginLeft: "6px"}}
                                                onClick={() => {changeOrderStatus(item.pk, StatusEnum.Denied).then(
                                                    dispatch({type: CHANGE_STATUS, payload: { index: index, status: StatusEnum.Denied }})
                                                )}}>Отклонить</Button>
                                    </>
                                }
                                { item.status == StatusEnum.Denied &&
                                    <>
                                        <Button variant="success" style={{marginRight: "6px"}}
                                                onClick={() => {changeOrderStatus(item.pk, StatusEnum.Pending).then(
                                                    dispatch({type: CHANGE_STATUS, payload: { index: index, status: StatusEnum.Pending }})
                                                )}}>Возобновить</Button>
                                    </>
                                }
                                { item.status == StatusEnum.InProgress &&
                                    <>
                                        <Button variant="success" style={{marginRight: "6px"}}
                                                onClick={() => {changeOrderStatus(item.pk, StatusEnum.Done).then(
                                                    dispatch({type: CHANGE_STATUS, payload: { index: index, status: StatusEnum.Done }})
                                                )}}>Завершить</Button>
                                        <Button variant="danger" style={{marginLeft: "6px"}}
                                                onClick={() => {changeOrderStatus(item.pk, StatusEnum.Denied).then(
                                                    dispatch({type: CHANGE_STATUS, payload: { index: index, status: StatusEnum.Denied }})
                                                )}}>Отклонить</Button>
                                    </>
                                }
                                </th>
                            </tr>
                        })}
                        </tbody>
                    </Table>
                </>
            );
        }
    } else {
