export const
    TOGGLE_TRUCK = 'TOGGLE_TRUCK',
    LOAD_TRUCKS = 'LOAD_TRUCKS',
    LOAD_BRANDS = 'LOAD_BRANDS',
    GET_SELECTED_TRUCKS_FROM_LOCAL = 'GET_SELECTED_TRUCKS_FROM_LOCAL',
    SET_SELECTED_TRUCKS_FOR_LOCAL = 'SET_SELECTED_TRUCKS_FROM_LOCAL'

export const reducer = (state, action) => {
// type, payload
    const { type, payload } = action
    switch(type) {
        case LOAD_TRUCKS:
            const fetchTrucks = async () => {
                const res = await fetch(`/api/cars/`)
                    .then((response) => {
                        return response.json();
                    }).catch(() => {
                        return {resultCount: 0, results: []}
                    })
                console.log(res)
                return res
            }

            const fetchBrands = async (tempTrucks) => {
                const res = []
                console.log(tempTrucks)
                for (const element of tempTrucks) {
                    const tempRes = await fetch(`/api/brands/${element.brand}`)
                        .then((response) => {
                            return response.json();
                        }).catch(() => {
                            return {resultCount: 0, results: []}
                        })
                    res.push(tempRes.title)
                }
                console.log(res)
                return res
            }
            fetchTrucks().then(trucks => {
                fetchBrands(trucks)
            })
            return {
                ...state,
                trucks: payload.trucks,
                brands: payload.brands
            }
        case TOGGLE_TRUCK:
            if (state.selectedTrucks.includes(payload.id)) {
                return {
                    ...state,
                    selectedTrucks: state.selectedTrucks.filter(id => id != payload.id)
                }
            } else {
                return {
                    ...state,
                    selectedTrucks: [...state.selectedTrucks, payload.id],
                }
            }

        case GET_SELECTED_TRUCKS_FROM_LOCAL:
            const raw = localStorage.getItem('selectedCards') || []
            return {
                ...state,
                selectedTrucks: JSON.parse(raw),
            }

        case SET_SELECTED_TRUCKS_FOR_LOCAL:
            localStorage.setItem('selectedCards', JSON.stringify(state.selectedTrucks))
            return state
        default:
            return state
    }
}
